import zipfileimport osdef backup(folder):    folder = os.path.abspath(folder)    number = 1    while True:        zipfile_name = os.path.basename(folder) + "_" + str(number) + ".zip"        if not os.path.exists(zipfile_name):            break        number += 1    zip_new = zipfile.ZipFile(zipfile_name, "w")    for folder_name, sub_folder, filenames in os.walk(folder):        print(f"Adding files in {folder_name}")        zip_new.write(folder_name)    for filename in filenames:        new_base = os.path.basename(folder)        if filename.startswith(new_base) and filename.endswith(".zip"):            continue        zip_new.write(os.path.join(folder_name, filename))    zip_new.close()    print("Done.")