import randomimport logginglogging.basicConfig(level=logging.DEBUG, format='%(asctime)s - %(levelname)s - %(message)s')logging.debug('Start of program.')guess = ''while guess not in ('heads', 'tails'):    print('Guess the coin toss! Enter heads of tails:')    guess = input()toss = random.choice(['heads', 'tails'])if toss == guess:    print('You got it!')else:    print('Nope! Guess again!')    assert toss in ('heads', 'tails'), 'There is an error!'    guess = input()    if toss == guess:        print("You got it!")    else:        print('Nope. You are really bad at this game')