def print_box(symbol, width, height):    if len(sym) != 1:        raise Exception('Symbol must be a single character string.')    if width <= 2:        raise Exception("Width must be greater than 2.")    if height <= 2:        raise Exception("Height must be greater than 2")    print(sym * width)    for i in range(height-2):        print(sym + (' ' * (width - 2)) + sym)    print(sym * width)for sym, w, h, in (("*", 4, 4), ('O', 20, 5), ('x', 1, 3), ('ZZ', 3, 30)):    try:        print_box(sym, w, h)    except Exception as err:        print(f"An exception happened: {err}")