# renameDates.py - Renames filenames with American MM-DD-YYYY date format# to European date formatimport osimport shutilimport redate_pattern = re.compile(r"""^(.#?)                ((0|1)?\d)-                ((0|1|2|3)?\d)-                ((19|20)\d\d)                (.*?)$)                """, re.VERBOSE)for am_file in os.listdir("."):    mo = date_pattern.search(am_file)    if mo is None:        continue    before_part = mo.group(1)    month_part = mo.group(2)    day_part = mo.group(4)    year_part = mo.group(6)    after_part = mo.group(8)    euro_file = before_part + day_part + "-" + month_part + "-" + year_part + after_part    abs_working = os.path.abspath(".")    am_file = os.path.join(abs_working, am_file)    euro_file = os.path.join(abs_working, euro_file)    print(f"Renaming '{am_file}' to '{euro_file}'...")