import tracebacktry:    market_2nd = {'ns': 'green', 'ew': 'red'}    mission_16th = {'ns': 'red', 'ew': 'green'}    def switch_lights(stoplight):        for key in stoplight.keys():            if stoplight[key] == 'green':                stoplight[key] = 'yellow'            elif stoplight[key] == 'yellow':                stoplight[key] = 'red'            elif stoplight[key] == 'red':                stoplight[key] = 'green'        assert 'red' in stoplight.values(), f'Neither light is red! {stoplight}'    switch_lights(mission_16th)except:    error_file = open("errorinfo.txt", "w")    error_file.write(traceback.format_exc())    error_file.close()    print("The traceback info was written to errorInfo.txt")