import tracebackimport logginglogging.basicConfig(filename= 'myProgramLog.txt', level=logging.DEBUG, format= '%(asctime)s - %(levelname)s - %(message)s')logging.debug('Start of program')def switch_lights(stoplight):    for key in stoplight.keys():        if stoplight[key] == 'green':            stoplight[key] = 'yellow'        elif stoplight[key] == 'yellow':            stoplight[key] = 'red'        elif stoplight[key] == 'red':            stoplight[key] = 'green'    assert 'red' in stoplight.values(), f'Neither light is red! {stoplight}'def factorial(n):    logging.debug(f"Start of factorial {n}")    total = 1    for i in range(1, n+1):        total *= i        logging.debug(f"i is {i}, total is {total}")    logging.debug(f"End of factorial {n} ")    return totaltry:    market_2nd = {'ns': 'green', 'sw': 'yellow', 'ew' : 'red'}    mission_16th = {'ns': 'red', 'sw': 'green','ew':'yellow'}    factorial(5)    switch_lights(mission_16th)    logging.debug('End of program')except:    error_file = open("errorinfo.txt", "w")    error_file.write(traceback.format_exc())    error_file.close()    print("The traceback info was written to errorInfo.txt")